import React from "react";
import Friend from "./friend";
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Divider from '@mui/material/Divider';
import Avatar from '@mui/material/Avatar';
import AvatarGroup from '@mui/material/AvatarGroup';
import IconButton from '@mui/material/IconButton';
import Tooltip from "@mui/material/Tooltip";
import { useSelector, useDispatch } from "react-redux";
import { setIsShowInfo } from '../../../features/currentSlice';
import CloseIcon from '@mui/icons-material/Close';
import FileCustomIcon from "../../react-file-icon/file-icon";

import moment from "moment";

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
    ({ theme, open }) => ({
      flexGrow: 1,
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginRight: -drawerWidth,
      ...(open && {
        transition: theme.transitions.create('margin', {
          easing: theme.transitions.easing.easeOut,
          duration: theme.transitions.duration.enteringScreen,
        }),
        marginRight: 0,
      }),
    }),
);
  
const DrawerHeader = styled('div')(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'space-between',
}))

const drawerWidth = 350;
const randomColor = () => "#" + Math.floor(Math.random()*16777215).toString(16);

const FriendInfo = () => {
    const dispatch = useDispatch();
    const open = useSelector(state => state.current.isShowInfo);
    const currentFriend = useSelector(state => state.current.friend);
    const handleDrawerClose = () => {
      dispatch(setIsShowInfo())
    };
    return (
      <Box sx={{ display: 'flex' }}>
        <Main open={open}>
          <Friend></Friend>
        </Main>
        <Drawer
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              position: 'relative'
            },
          }}
          variant="persistent"
          anchor="right"
          open={open}
        >
          <DrawerHeader>
            <div className="friend">Thông tin bạn bè</div>
            <IconButton onClick={handleDrawerClose}>
              <CloseIcon></CloseIcon>
            </IconButton>
          </DrawerHeader>
          <Divider />
          {currentFriend._id ?
            <div className="friend-info">
                <div style={{display: 'flex', flexDirection: "column", justifyContent: "center", alignItems: "center"}}>
                        <div style={{padding: 10}}>
                            <Avatar></Avatar>
                            <h4>{c.name}</h4>
                        </div>
                        <div style={{width: "100%"}}>
                            <p>{`ID: ${findFriendRes[0]._id}`}</p>
                            <p>{`Sở hữu: ${findFriendRes[0].own_files.length}`}</p>
                            <p>{`Chia sẻ: ${findFriendRes[0].shared_files.length}`}</p>
                            <p>{`Bạn bẻ: ${findFriendRes[0].friend.accepted.length}`}</p>
                        </div>
                        <div>
                            <ButtonModal friendRes={findFriendRes}></ButtonModal>
                        </div>
                    </div>
            </div>
            :
            <div>
              
            </div>
          }
        </Drawer>
      </Box>
    )
}

export default FriendInfo;
