import React from "react";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getFriend, handleFriend, findFriend, setNullFindFriendRes } from "../../../features/userSlice";
import { setFriend, setIsShowInfo } from "../../../features/currentSlice";
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Loading from "../../loading";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import Divider from "@mui/material/Divider";
import Modal from '@mui/material/Modal';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import InputBase from '@mui/material/InputBase';
import PersonSearchIcon from '@mui/icons-material/PersonSearch';
import Tooltip from '@mui/material/Tooltip';
import InfoIcon from '@mui/icons-material/Info';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import NoFriend from '../../../assets/img/no_friend.jpg';
import NoFriendFound from '../../../assets/img/no_friend_found.png';
import FindFriend from '../../../assets/img/find_friend.jpg'

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: 'none',
    boxShadow: 24,
    borderRadius: 1
};

function TabPanel(props) {
    const { children, value, index, ...other } = props;
  
    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box sx={{ p: 2 }}>
            {children}
          </Box>
        )}
      </div>
    );
}

function a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
}

const ButtonHandleFriend = ({handleAdd, handleDelete, index}) => {
    if(index === 0) {
        return(<>
            <Button
            onClick={handleDelete}
            variant="outlined"
        >Xóa bạn bè</Button>
        </>)
    }
    if(index===1) {
        return(<>
            <Button
                onClick={handleAdd}
                variant="contained"
            >Chấp nhận</Button>
            <Button
            onClick={handleDelete}
            variant="outlined"
            >Xóa yêu cầu</Button>
        </>)
    }
    else return(<>
        <Button
        onClick={handleDelete}
        variant="outlined"
        >Hủy yêu cầu</Button>
    </>)
}

const ButtonModal = ({friendRes}) => {
    const friend = useSelector(state => state.user.user.user_info.friend);
    const dispatch = useDispatch();

    const addFriend = (id) => {
        dispatch(handleFriend({type: "acceptRequest", id}))
    }
    const sendReqFriend = (id) => {
        dispatch(handleFriend({type: "sendRequest", id}))
    }
    const checkArray = (value) => {
        return value === friendRes[0]._id
    }

    if(friend.accepted.some(checkArray)) {
        return <Button disabled>Đã là bạn</Button>
    }
    else if(friend.request.some(checkArray)) {
        return <Button onClick={() => addFriend(friendRes[0]._id)}>Chấp nhận kết bạn</Button>
    }
    else if(friend.myRequest.some(checkArray)) {
        return <Button disabled>Đã gửi lời mời</Button>
    }
    else {
        return <Button onClick={() => sendReqFriend(friendRes[0]._id)}>Kết bạn</Button>
    }
}

const ModalFriend = ({handleClose, open}) => {
    const [value, setValue] = React.useState('')
    const dispatch = useDispatch();
    const findFriendRes = useSelector(state => state.user.friend.findFriendRes);
    const handleFind = () => {
        dispatch(findFriend(value));
    }
    
    return (
        <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
        >
            <Box sx={style}>
                <div style={{display: 'flex', alignItems: "center", justifyContent: "space-between", padding: 5}}>
                    <h4 style={{paddingLeft: 10}}>Tìm kiếm bạn bè</h4>
                    <IconButton aria-label="search-friend" onClick={handleClose}>
                        <CloseIcon />
                    </IconButton>
                </div>
                <Divider></Divider>
                <div>   
                    <IconButton sx={{ p: '10px' }} aria-label="menu">
                        <PersonSearchIcon />
                    </IconButton>
                    <InputBase
                        sx={{ ml: 1, flex: 1, borderBottom: 1, width: "80%" }}
                        placeholder="Tên bạn bè, id"
                        inputProps={{ 'aria-label': 'Tên bạn bè' }}
                        autoFocus={true}
                        onChange={(e) => setValue(e.currentTarget.value)}
                    />
                </div>
                {!findFriendRes ? 
                    <div style={{display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                        <img src={FindFriend} alt="Find Friend" width="200px" style={{margin: "1rem"}}/>
                        <p style={{fontWeight: 500}}>Nhập ID để tìm bạn</p>
                        <sub>Kết bạn để khám phá nhiều điều mới</sub>
                    </div>
                    : Object.keys(findFriendRes).length === 0 ? 
                    <div style={{display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                        <img src={NoFriendFound} alt="No Friend Found" width="200px" style={{margin: "1rem"}}/>
                        <p style={{fontWeight: 500}}>Không tìm thấy bạn</p>
                        <sub>Hãy tìm lại</sub>
                    </div>
                    :
                    <div className="friend-info">
                    <div style={{width: "100%", display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column'}}>
                        <Avatar sx={{backgroundColor: randomColor}}></Avatar>
                        <h4>{currentFriend.name}</h4>
                    </div>
                    <p>Thuộc tính</p>
                    <table className="friend-info__table">
                        <tbody>
                        <tr>
                            <td>ID</td>
                            <td>{currentFriend._id}</td>
                        </tr>
                        <tr>
                            <td>Sở hữu</td>
                            <td>{currentFriend.own_files.length}</td>
                        </tr>
                        <tr>
                            <td>Chia sẻ</td>
                            <td>{currentFriend.shared_files.length}</td>
                        </tr>
                        <tr>
                            <td>Bạn bè</td>
                            <td>{currentFriend.friend.accepted.length}</td>
                        </tr>
                        </tbody>
                    </table>
                </div>}
                <div style={{display: "flex", justifyContent: "flex-end", alignItems: "center", margin: 10}}>
                    <Button variant="outlined" onClick={handleClose}>Hủy</Button>
                    <Button variant="contained" onClick={handleFind}>Tìm kiếm</Button>
                </div>
            </Box>
        </Modal>
    );
}

const FriendTable = ({friend, index}) => {
    const dispatch = useDispatch();
    const currentFriend = useSelector(state => state.current.friend);
    
    const selectFriend = (value) => {
        dispatch(setFriend(value))
    }

    const handleAdd = async (id) => {
        if(index === 1) {
            await dispatch(handleFriend({type: "acceptRequest", id}));
            await dispatch(getFriend("reqFriend"));
        }
    }
    const handleDelete = async (id) => {
        if(index === 0) {
            await dispatch(handleFriend({type: "deleteFriend", id}));
            await dispatch(getFriend("friend"));
        }
        else if(index === 1) {
            await dispatch(handleFriend({type: "deleteRequest", id}));
            await dispatch(getFriend("reqFriend"));
        }
        else if(index === 2) {
            await dispatch(handleFriend({type: "deleteMyRequest", id}));
            await dispatch(getFriend("myReqFriend"));
        }
    }
    if(friend.length === 0) {
        return(
            <div style={{display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                <img src={NoFriend} alt="No Own File" width="400px" style={{margin: "1rem"}}/>
                <p style={{fontWeight: 500}}>Hiện tại bạn không có bạn</p>
                <sub>Chắc bạn tự kỷ :))</sub>
            </div>
        )
    }
    return(<>
        <List dense sx={{ width: '100%', bgcolor: 'background.paper' }}>
        {friend.map((value) => {
            const labelId = `checkbox-list-secondary-label-${value._id}`;
            return (
            <ListItem
                key={value._id}
                value={value._id}
                selected={currentFriend._id === value._id}
                onClick={(event) => {
                    if(event.currentTarget.getAttribute("value")===currentFriend._id) return;
                    selectFriend(value)
                }}
                secondaryAction={
                    <>
                        {<ButtonHandleFriend handleAdd={() => handleAdd(value._id)} handleDelete={() => handleDelete(value._id)} index={index}></ButtonHandleFriend>}
                    </>
                }
                disablePadding
            >
                <ListItemButton>
                <ListItemAvatar>
                    <Avatar></Avatar>
                </ListItemAvatar>
                <ListItemText id={labelId} primary={value.name} />
                </ListItemButton>
            </ListItem>
            );
            <Divider variant="inset" component="li" />
        })}
        </List>
    </>)
}

const Friend = () => {
    const dispatch = useDispatch();
    const isLoad = useSelector(state => state.user.isLoading)
    const friend = useSelector(state => state.user.friend);
    const [value, setValue] = React.useState(0);
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => setOpen(true);

    const handleClose = () => {
        setOpen(false);
        dispatch(setNullFindFriendRes());
    }
    const handleChange = (event, newValue) => {
        setValue(newValue);
        dispatch(setFriend({
            _id: null
        }))
    };

    const showFriendInfo = () => {
        dispatch(setIsShowInfo());
    }

    useEffect(() => {
        const typeArray = ["friend", "reqFriend", "myReqFriend"]
        dispatch(getFriend(typeArray[value]));
    }, [value])
    if(isLoad) {
        return <Loading></Loading>
    }
    else return(
        <>
            <div>
                <Box sx={{ width: '100%' }}>
                    <Box sx={{ borderBottom: 1, borderColor: 'divider', display: "flex", alignItems: "center", justifyContent: "space-between"}}>
                        <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
                            <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Bạn bè" {...a11yProps(0)} />
                            <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Yêu cầu kết bạn" {...a11yProps(1)} />
                            <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Lời mời đã gửi" {...a11yProps(2)} />
                        </Tabs>
                        <div style={{marginRight: "10px"}}>
                            <Tooltip title="Thêm bạn bè">
                                <IconButton onClick={handleOpen}>
                                    <PersonAddIcon />
                                </IconButton>
                            </Tooltip>
                            <ModalFriend handleClose={handleClose} open={open}></ModalFriend>
                            <Tooltip title="Xem chi tiết">
                                <IconButton onClick={showFriendInfo}>
                                    <InfoIcon />
                                </IconButton>
                            </Tooltip>
                        </div>
                    </Box>
                    <TabPanel value={value} index={0}>
                        <FriendTable friend={friend.friend} index={0} type="accepted"></FriendTable>
                    </TabPanel>
                    <TabPanel value={value} index={1}>
                        <FriendTable friend={friend.friend} index={1} type="request"></FriendTable>
                    </TabPanel>
                    <TabPanel value={value} index={2}>
                        <FriendTable friend={friend.friend} index={2} type="myRequest"></FriendTable>
                    </TabPanel>
                </Box>
            </div>
        </>
    )
}

export default Friend;
