import {createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import sendReq from '../helper/sendReqLogin'

const sendReqToServer = createAsyncThunk(
    "Req Login to Server",
    async (configReq, thunkAPI) => {
        const res = await sendReq(configReq)
        return res.data
    }
)

const authenticate = createSlice({
    name: "authenticate",
    initialState: {
        isAuthenticated: false,
        message: {
            type: "error",
            mess: null
        }
    },
    reducers : {
        setIsAuthenticate : (state, action) => {
            state.isAuthenticated = action.payload;
        },
        addAuthenticateMess: (state, action) => {
            state.message = action.payload;
        }
    }
})

export const {setIsAuthenticate, addAuthenticateMess} = authenticate.actions;
export default authenticate.reducer;
