import {createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import sendReq from '../helper/sendReqToServer'

export const sendReqLogin = createAsyncThunk(
    "localhost:4000/account/signin",
    async (dataReq, thunkAPI) => {
        const configReq = {
            method: 'POST',
            url: '/account/signin',
            header: {
                "Content-Type": 'application/x-www-form-urlencoded;charset=utf-8'
            },
            data: dataReq
        }
        const res = await sendReq(configReq)
        return res.data
    }
)

export const sendReqReLogin = createAsyncThunk(
    "localhost:4000/",
    async (thunkAPI) => {
        const configReq = {
            method: 'get',
            url: '/',
            header: {
                "Content-Type": 'text/plain; charset=utf-8'
            },
            data: ""
        }
        const res = sendReq(configReq);
        return res.data
    }
)

export const sendReqRegister = createAsyncThunk(
    "localhost:4000/",
    async (dataReq,thunkAPI) => {
        const configReq = {
            method: 'POST',
            url: '/account/signup',
            header: {
                "Content-Type" : 'application/x-www-form-urlencoded;charset=utf-8'
            },
            data: dataReq
        }
        const res = sendReq(configReq);
        return res.data
    }
)

const authenticate = createSlice({
    name: "authenticate",
    initialState: {
        auth : {
            isAuthenticated: false,
            message: {
                type: null,
                mess: null
            },
        },
        isLoading : false
    },
    reducers : {
        setMess : (state, action) => {
            state.auth.message = {
                type: action.payload.type,
                mess: action.payload.mess
            }
        },
        setIsLoad : (state, action) => {
            state.isLoading = action.payload
        }
    },
    extraReducers : (builder) => {
        builder.addCase(sendReqLogin.pending || sendReqReLogin.pending || sendReqRegister.pending, (state, action) => {
            state.isLoading = true;
        })
        builder.addCase(sendReqLogin.fulfilled || sendReqReLogin.fulfilled ||sendReqRegister.fulfilled, (state, action) => {
            if(state.auth !== action.payload.isAuthenticated) {
                state.auth = action.payload
            }
            else {
                state.auth.message = action.payload.message
            }
            state.isLoading = false;
        })
    }
})

export const {setMess, setIsLoad} = authenticate.actions;
export default authenticate.reducer;
