import React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import './css/login.css'
import { sendReqToServer } from '../features/authenticateSlice';
import qs from 'qs';
import $ from 'jquery';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux'


function TabPanel(props) {
    const { children, value, index, ...other } = props;
  
    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        className={'tab'}
        {...other}
      >
        {value === index && (
            <div className="main-tab">{children}</div>
        )}
      </div>
    );
}

function a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`
    };
}

function Login(props) {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const [value, setValue] = React.useState(0);
    const navigate = useNavigate();
    const dispatch = useDispatch()
    let err = {
        isTrue : false,
        mess : ""
    }
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    const handleChangeTab = () => {
        setValue(value === 0 ? 1 : 0)
    }

    const handleClick = async () => {
        if(value === 0) {
            const configReq = {
                method: 'POST',
                url: '/authenticate/login',
                header: 'application/x-www-form-urlencoded;charset=utf-8',
                data: qs.stringify({
                    "username": `${String($('#username').val())}`,
                    "password": `${String($('#password').val())}`
                })
            }
            const res = dispatch(sendReqToServer(configReq))
            if(auth) {
                navigate('/document');
            }
            else {
                err = {
                    isTrue: true,
                    mess: res.message
                }
            }
            console.log(err.mess)
        } 
    }

    return(
        <div className='login-page'>
            <div className='login-form'>
                <h1>DoS</h1>
                <Tabs value={value} onChange={handleChange} aria-label="basic tabs example" className='Muti-tab' variant="fullWidth">
                    <Tab label="Đăng nhập" {...a11yProps(0)}/>
                    <Tab label="Đăng ký" {...a11yProps(1)} />
                </Tabs>
                <TabPanel value={value} index={0}>
                    <div className='login-form__header'>
                        <h2>Đăng nhập</h2>
                        <h4>Tiếp tục với tài khoản DShare</h4>
                    </div>
                    <div className='login-form__body'>
                        <TextField id="username" label="Tên đăng nhập" error={err.isTrue} variant="outlined" className='lgf_b-textField' margin="normal"/>
                        <TextField id="password" label="Mật khẩu" error={err.isTrue} variant="outlined" className='lgf_b-textField'margin="normal"/>
                    </div>
                    <div className='login-form__bottom'>
                        <Button variant="text" size="small" className='btn-registration' onClick={handleChangeTab}>Tạo tài khoản</Button>
                        <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>Đăng nhập</Button>
                    </div>
                </TabPanel>
                <TabPanel value={value} index={1} className="tab">
                    <div className='login-form__header'>
                        <h2>Đăng ký</h2>
                        <h4>Chào mừng bạn đến với DShare</h4>
                    </div>
                    <div className='login-form__body'>
                        <TextField label="Tên đăng nhập" variant="outlined" className='lgf_b-textField' margin="normal"/>
                        <TextField label="Mật khẩu" variant="outlined" className='lgf_b-textField'margin="normal"/>
                        <TextField label="Mật xác nhận mật khẩu" variant="outlined" className='lgf_b-textField'margin="normal"/>
                    </div>
                    <div className='login-form__bottom'>
                        <Button variant="text" size="small" className='btn-registration' onClick={handleChangeTab}>Đăng nhập</Button>
                        <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>Tạo tài khoản</Button>
                    </div>
                </TabPanel>
            </div>
        </div>
    )
}

export default Login;
