import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import {setIsAuthenticate, addAuthenticateMess} from "../features/authenticateSlice";
import sendReqLogin from '../helper/sendReqLogin';
import Loading from '../component/loading';

const ReRoute = ({children}) => {
    const auth = useSelector((state) => state.authenticate.isAuthenticated);
    const dispatch = useDispatch();
    const [isLoading, setIsLoading] = React.useState(true)

    const sendReqToServer = async () => {
        const configReq = {
            method: 'get',
            url: '/',
            header: "Content-Type': 'text/plain; charset=utf-8",
            data: ""
        }
        const res = await sendReqLogin(configReq);
        setIsLoading(!isLoading);
        if(res.data.isAuthenticated) {
            dispatch(setIsAuthenticate(true))
        }
        else {
            dispatch(addAuthenticateMess(res.data.message))
        }
    }

    React.useEffect(() => {
        sendReqToServer()
    })

    return (
        isLoading ? <Loading></Loading> : auth ? <Navigate to="/document"/> : children
    )
}

export default ReRoute;
