import React from "react";
import { styled, alpha } from '@mui/material/styles';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import DriveFolderUploadIcon from '@mui/icons-material/DriveFolderUpload';
import Divider from '@mui/material/Divider';
import CreateNewFolderIcon from '@mui/icons-material/CreateNewFolder';
import AddIcon from '@mui/icons-material/Add';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import { upLoadFile, getFile } from "../../../features/fileSlice";
import { useDispatch, useSelector } from "react-redux";
import jszip from 'jszip';

const Input = styled('input')({
  display: 'none',
});

const StyledMenu = styled((props) => (
  <Menu
    elevation={0}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'right',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'right',
    }}
    {...props}
  />
))(({ theme }) => ({
  '& .MuiPaper-root': {
    borderRadius: 6,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color:
      theme.palette.mode === 'light' ? 'rgb(55, 65, 81)' : theme.palette.grey[300],
    boxShadow:
      'rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px',
    '& .MuiMenu-list': {
      padding: '4px 0',
    },
    '& .MuiMenuItem-root': {
      '& .MuiSvgIcon-root': {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      '&:active': {
        backgroundColor: alpha(
          theme.palette.primary.main,
          theme.palette.action.selectedOpacity,
        ),
      },
    },
  },
}));

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

export default function Menus(props) {
  const dispatch = useDispatch();
  const path = useSelector(state => state.current.path);
  const [openModal, setOpenModal] = React.useState(false);
  const handleOpenModal = () => setOpenModal(true);
  const handleCloseModal = () => setOpenModal(false);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

   const [folderName, setFolderName] = React.useState("");

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleChange = async (e) => {
    const data = new FormData();
    const fileList = e.target.files;
    
    Object.values(fileList).forEach(file => {
      console.log(file.webkitRelativePath)
      data.append("path", path)
      //data.append('parent', file.webkitRelativePath === "" ? path : path + file.webkitRelativePath.split('/').at(-2))
      data.append('file', file, file.name);
      
    })


    if(e.target.files.length === 1) {
      await dispatch(upLoadFile({data: data, type: 'single'}))
    }
    else {
      await dispatch(upLoadFile({data: data, type: 'multiple'}))
    }
    await props.handleClick();
    dispatch(getFile("own"))
    handleClose()
  }



  return (
    <div>
      <Button
        id="demo-customized-button"
        aria-controls={open ? 'demo-customized-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        variant="contained"
        disableElevation
        onClick={handleClick}
        startIcon={<AddIcon />}
        sx = {{margin: 2}}
      >
        Thêm
      </Button>
      <Modal
        open={openModal}
        onClose={handleCloseModal}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <form>
            <h4>Tạo thư mục mới</h4>
            <input type="text" placeholder="Nhập tên thư mục"></input>
            <div>
              <Button type='button'>Hủy</Button>
              <Button type='submit'>Tạo</Button>
            </div>
          </form>
        </Box>
      </Modal>
      <StyledMenu
        id="demo-customized-menu"
        MenuListProps={{
          'aria-labelledby': 'demo-customized-button',
        }}
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
      >
        <MenuItem onClick={() => {handleClose(); handleOpenModal()}} disableRipple>
          <CreateNewFolderIcon />
          Tạo thư mục
        </MenuItem>
        <Divider sx={{ my: 0.5 }} />
        <MenuItem disableRipple>
          <label htmlFor="icon-button-file">
            <Input accept="*" id="icon-button-file" type="file" name="file" onChange={handleChange} multiple/>
            <UploadFileIcon />
            Tải lên tệp tin
          </label>
        </MenuItem>
        {/*<MenuItem disableRipple>
          <label htmlFor="icon-button-folder">
            <input type="file" id="icon-button-folder" name="file"  onChange={handleChange} webkitdirectory='true' multiple/>
            <DriveFolderUploadIcon />
            Tải lên thư mục
          </label>
        </MenuItem>*/}
      </StyledMenu>
    </div>
  );
}
