import {createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import sendReq from '../helper/sendReqToServer'

export const getUserData = createAsyncThunk(
    "localhost:4000/user",
    async (thunkAPI) => {
        const configReq = {
            method: 'get',
            url: '/user',
            header: {
                "Content-Type": "text/plain"
            },
            data: ""
        }
        const res = await sendReq(configReq)
        return res.data
    }
)

export const getFriend = createAsyncThunk(
    "localhost:4000/user/friend",
    async (thunkAPI)  => {
        const configReq = {
            method: 'get',
                url: '/user/friend',
                header: {
                    "Content-Type": "text/plain"
                },
                data: ""
        }
        const res = await sendReq(configReq)
        return res.data
    }
)

const user = createSlice({
    name: "user",
    initialState: {
        isLoading: true,
        user : null,
        friend: {
            request: [],
            accep
        }
    },
    reducers: {

    },
    extraReducers : (builder) => {
        builder.addCase(getUserData.pending, (state, action) => {
            state.isLoading = true
        })
        builder.addCase(getUserData.fulfilled, (state, action) => {
            state.user = action.payload
            state.isLoading = false
            console.log(state.user)
        })
        builder.addCase(getFriend.pending, (state, action) => {
            state.isLoading = true
        })
        builder.addCase(getFriend.fulfilled, (state, action) => {
            state.friend = action.payload
            state.isLoading = false
            console.log(state.friend)
        })
    }
})



export const {} = user.actions;
export default user.reducer;
