import React, {useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import {setIsAuthenticate, addAuthenticateMess} from "../features/authenticate/authenticateSlice";
import sendReqLogin from '../helper/sendReqLogin';

const ReRoute = ({children}) => {
    const auth = useSelector(state => state.authenticate.isAuthenticate);
    const dispatch = useDispatch();
    const sendReqToServer = async () => {
        const configReq = {
            method: 'get',
            url: '/',
            header: "Content-Type': 'text/plain; charset=utf-8",
            data: ""
        }
        const res = await sendReqLogin(configReq);
        if(res.data.isAuthenticated) {
            dispatch(setIsAuthenticate(true))
        }
        else {
            dispatch(addAuthenticateMess(res.data.message))
        }
    }
    useEffect(() => {
        sendReqToServer();
    }, [])
    return (auth ? <Navigate to="/document"/> : children)
}

export default ReRoute;
