import React from "react";
import Divider from "@mui/material/Divider";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import LinkIcon from '@mui/icons-material/Link';
import GroupAddIcon from '@mui/icons-material/GroupAdd';
import CloseIcon from '@mui/icons-material/Close';
import DownloadIcon from '@mui/icons-material/Download';
import InfoIcon from '@mui/icons-material/Info';
import {useDispatch, useSelector} from 'react-redux';
import {setIsShowInfo} from '../../../features/currentSlice';
import { downLoadFile } from "../../../features/fileSlice";
import Breadcrumbs from '@mui/material/Breadcrumbs';
import Link from '@mui/material/Link';
import {Modal, Box, Button} from "@mui/material";
import { setPath } from "../../../features/currentSlice";
import FileCustomIcon from "../../react-file-icon/file-icon";
import FolderIcon from '../../../assets/img/folder.svg';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Checkbox from '@mui/material/Checkbox';
import ListItemIcon from '@mui/material/ListItemIcon';
import { getFriend } from "../../../features/userSlice";
import Loading from "../../loading";
import InfiniteScroll from "react-infinite-scroll-component";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    borderRadius: 1,
    boxShadow: 24
};

const ShareModal  = ({handleCloseShareModal, openShareModal, file, isShow}) => {
    const friend = useSelector(state => state.user.friend.friend);
    console.log(friend)
    const dispatch = useDispatch();
    const [checked, setChecked] = React.useState([]);
    const [hasMore, setHasMore] = React.useState(true)
    const [items, setItems] = React.useState([]);
    const [page, setPage] = React.useState(0);
    
    const handleToggle = (value) => () => {
        const currentIndex = checked.indexOf(value);
        const newChecked = [...checked];

        if (currentIndex === -1) {
            newChecked.push(value);
        } else {
            newChecked.splice(currentIndex, 1);
        }

        setChecked(newChecked);
    };
    const handleSubmit = (e) => {
        e.preventDefault();
    }

    const loadFriend = async () => {
        console.log("A")
        if(friend[0] === "END") {
            setHasMore(false);
        }
        else {
            const newItems = [...items];
            await dispatch(getFriend({type: "friend", page: page}));
            friend.map((value) => {
                if(value === "END") return;
                return newItems.push(value)
            })
            setItems(newItems);
            setPage(page + 1);
        }
    }

    React.useEffect(() => {
        dispatch(getFriend({type: "friend", page: 0}));
    },[])

    return(
        <Modal
            open={openShareModal}
            onClose={handleCloseShareModal}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
            >
            <Box sx={style}>
            <div style={{display: 'flex', alignItems: "center", justifyContent: "space-between", padding: 5}}>
                        <h4 style={{paddingLeft: 10}}>Chia sẻ</h4>
                        <IconButton aria-label="search-friend" onClick={handleCloseShareModal}>
                            <CloseIcon />
                        </IconButton>
                </div>
                <Divider></Divider>
                {file._id && <div className="file-name">{ file.metadata.isDir ?
                    <div className="file-name">
                        <div className="icon" style={{display: "flex", justifyContent: 'center', alignItems: 'center'}}>
                        <img src={FolderIcon} style={{width: '100%'}} alt="folder"/>
                        </div>
                        {file.name}
                    </div>
                    :<FileCustomIcon ext={file.metadata.ext}></FileCustomIcon>} {file.metadata.originalname}
                </div>}
                <form onSubmit={handleSubmit}>
                    <List dense sx={{ width: '100%', bgcolor: 'background.paper' }}>
                        <div id="scrollableDiv" style={{height: 300, overflow:"auto"}}>
                            <InfiniteScroll
                                dataLength={1}
                                next={loadFriend}
                                hasMore={true}
                                loader={<Loading />}
                                height={400}
                                scrollableTarget="scrollableDiv"
                            >
                                {items.map((value) => {
                                    const labelId = `checkbox-list-secondary-label-${value._id}`;
                                    return(
                                        <ListItem
                                        key={value._id}
                                        value={value._id}
                                        disablePadding
                                        >
                                            <ListItemButton role={undefined} onClick={handleToggle(value)} dense>
                                                <ListItemIcon>
                                                    <Checkbox
                                                    edge="start"
                                                    checked={checked.indexOf(value) !== -1}
                                                    tabIndex={0}
                                                    disableRipple
                                                    inputProps={{ 'aria-labelledby': labelId }}
                                                    />
                                                </ListItemIcon>
                                            <ListItemAvatar>
                                                <Avatar></Avatar>
                                            </ListItemAvatar>
                                            <ListItemText id={labelId} primary={value.name} />
                                            </ListItemButton>
                                        </ListItem>
                                    );
                                })}
                            </InfiniteScroll>
                        </div>
                    </List>
                    <div style={{display: "flex", justifyContent: "flex-end", alignItems: "center", margin: 10}}>
                        <Button variant="outlined" type='button' onClick={handleCloseShareModal}>Hủy</Button>
                        <Button variant="contained" type='submit'>Chia sẻ</Button>
                    </div>
                </form>
            </Box>
        </Modal>
    )
}


const DocumentHeader = () => {
    const tabs = useSelector(state => state.current.tabs);
    const selectedFile = useSelector(state => state.current.file);
    const path = useSelector(state =>  state.current.path);
    const [openShareModal, setOpenShareModal] = React.useState(false);
    const dispatch = useDispatch()

    const handleClick = () => {
        dispatch(setIsShowInfo());
    }

    const  handleClickLink = (e) => {
        e.preventDefault()

    }

    const handleDownload = async () => {
        if(selectedFile === "") {
            return;
        }
        await dispatch(downLoadFile(selectedFile));
    }

    const handleShare = async () => {
        setOpenShareModal(true);
    }

    const handleCloseShareModal = () => {
        setOpenShareModal(false)
    }

    return(
        <>
            <div className="document-header">
                {tabs === 0 ? <Breadcrumbs>
                    <Link underline="hover" color="inherit" onClick={handleClickLink} sx={{cursor: 'pointer'}}>
                        Tài liệu của tôi
                    </Link>
                    {path.split('/').map((value, index) => {
                        
                        if(value==="") return;
                        else if(path.split('/').length - 1 === index) {
                            return <>{value}</>;
                        }
                        else {
                            return(
                                <Link underline="hover" color="inherit" onClick={handleClickLink} sx={{cursor: 'pointer'}}>
                                    {value}
                                </Link>
                            )
                        }
                    })}
                </Breadcrumbs> : "Được chia sẻ với tôi"}
                <div className="document-header__list-ctrl">
                    <ShareModal handleCloseShareModal={handleCloseShareModal} openShareModal={openShareModal} file={selectedFile} isShow={openShareModal}></ShareModal>   
                    {selectedFile._id ?
                    <div style={{display: "flex"}}>
                        <Tooltip title="Nhận đường liên kết">
                            <IconButton>
                                <LinkIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Chia sẻ">
                            <IconButton onClick={handleShare}>
                                <GroupAddIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Tải về">
                            <IconButton onClick={handleDownload}>
                                <DownloadIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Xóa">
                            <IconButton>
                                <DeleteIcon />
                            </IconButton>
                        </Tooltip>
                        <Divider orientation="vertical" variant="middle" flexItem />
                    </div> : ""}
                    <Tooltip title="Xem chi tiết">
                        <IconButton onClick={handleClick}>
                            <InfoIcon />
                        </IconButton>
                    </Tooltip>
                </div>
            </div>
            <Divider></Divider>
        </>
    )
}

export default DocumentHeader;
