import React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Alert from '@mui/material/Alert';
import './css/login.css'
import { sendReqToServer } from '../features/authenticateSlice';
import CircularProgress from '@mui/material/CircularProgress';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faShareFromSquare} from '@fortawesome/free-solid-svg-icons'
import qs from 'qs';
import $ from 'jquery';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux'


function TabPanel(props) {
    const { children, value, index, ...other } = props;
  
    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        className={'tab'}
        {...other}
      >
        {value === index && (
            <div className="main-tab">{children}</div>
        )}
      </div>
    );
}

function a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`
    };
}

function Login() {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const message = useSelector(state => state.authenticate.auth.message);
    const isLoad = useSelector(state => state.authenticate.isLoading)
    const [value, setValue] = React.useState(0);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    console.log(isLoad)

    const handleChangeTab = () => {
        setValue(value === 0 ? 1 : 0)
    }

    const handleClick = (event) => {
        const username = String($('#username').val());
        const password = String($('#password').val());
        if(username === "" || password === "") {
            event.preventDefault()
            return;
        }
        if(value === 0) {
            const configReq = {
                method: 'POST',
                url: '/authenticate/login',
                header: 'application/x-www-form-urlencoded;charset=utf-8',
                data: qs.stringify({
                    "username": `${username}`,
                    "password": `${password}`
                })
            }
            dispatch(sendReqToServer(configReq))
            if(auth) {
                navigate('/document');
            }
        } 
    }

    return(
        <div className='login-page'>
            <div className='login-form'>
                <h1><FontAwesomeIcon icon={faShareFromSquare} id="icon-share"/>Dshare</h1>
                <Tabs value={value} onChange={handleChange} aria-label="basic tabs example" className='Muti-tab' variant="fullWidth">
                    <Tab label="Đăng nhập" {...a11yProps(0)}/>
                    <Tab label="Đăng ký" {...a11yProps(1)} />
                </Tabs>
                <TabPanel value={value} index={0}>
                    <div className='login-form__header'>
                        <h2>Đăng nhập</h2>
                        <h4>Tiếp tục với tài khoản Dshare</h4>
                    </div>
                    <div className='login-form__body'>
                        {message.isErr && <Alert severity="error">Sai tên đăng nhập hoặc mật khẩu !</Alert>}
                        <TextField id="username" label="Tên đăng nhập" error={message.isErr} variant="outlined" className='lgf_b-textField' margin="normal"/>
                        <TextField id="password" label="Mật khẩu" error={message.isErr} variant="outlined" className='lgf_b-textField'margin="normal"/>
                    </div>
                    <div className='login-form__bottom'>
                        <Button variant="text" size="small" className='btn-registration' onClick={handleChangeTab}>Tạo tài khoản</Button>
                        <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>{isLoad ? <CircularProgress color="secondary" /> : "Đăng nhập"}</Button>
                    </div>
                </TabPanel>
                <TabPanel value={value} index={1} className="tab">
                    <div className='login-form__header'>
                        <h2>Đăng ký</h2>
                        <h4>Chào mừng bạn đến với Dshare</h4>
                    </div>
                    <div className='login-form__body'>
                        div.lgf-name
                        <TextField label="Tên đăng nhập" variant="outlined" className='lgf_b-textField' margin="normal"/>
                        <TextField label="Mật khẩu" variant="outlined" className='lgf_b-textField'margin="normal"/>
                        <TextField label="Mật xác nhận mật khẩu" variant="outlined" className='lgf_b-textField'margin="normal"/>
                    </div>
                    <div className='login-form__bottom'>
                        <Button variant="text" size="small" className='btn-registration' onClick={handleChangeTab}>Đăng nhập ngay</Button>
                        <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>Tạo tài khoản</Button>
                    </div>
                </TabPanel>
            </div>
        </div>
    )
}

export default Login;
