import React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import './css/login.css'
import { sendReqToServer,setMessNull } from '../features/authenticateSlice';
import CircularProgress from '@mui/material/CircularProgress';
import qs from 'qs';
import $ from 'jquery';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import Logo from "../assets/img/document-share.png";

function Signin() {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const message = useSelector(state => state.authenticate.auth.message);
    const isLoad = useSelector(state => state.authenticate.isLoading)
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const handleChange = () => {
        dispatch(setMessNull());
        navigate('/signup');
    };

    const handleClick = (event) => {
        const username = String($('#username').val());
        const password = String($('#password').val());
        if(username === "" || password === "") {
            event.preventDefault()
            return;
        }
        const configReq = {
            method: 'POST',
            url: '/account/login',
            header: 'application/x-www-form-urlencoded;charset=utf-8',
            data: qs.stringify({
                "username": `${username}`,
                "password": `${password}`
            })
        }
        dispatch(sendReqToServer(configReq))
        if(auth) {
            navigate('/document');
        }
    }

    return(
        <div className='account-page'>
            <div className='account-form'>
                <div className='account-form__img'>
                    
                </div>
                <div className='account-form__form'>
                    
                </div>
                <img src={Logo} alt="Logo"/>
                <div className='account-form__header'>
                    <h2>Đăng nhập</h2>
                    <h4>Tiếp tục với tài khoản Dshare</h4>
                </div>
                <div className='account-form__body'>
                    {message.type === "error" && <Alert severity={message.type}>{message.mess}</Alert>}
                    <TextField id="username" label="Tên người dùng" error={message.type === "error" ? true : false} variant="outlined" className='lgf_b-textField' margin="normal"/>
                    <TextField id="password" label="Mật khẩu" error={message.type === "error" ? true : false} variant="outlined" className='lgf_b-textField'margin="normal"/>
                </div>
                <div className='account-form__bottom'>
                    <Button variant="text" size="small" className='btn-registration' onClick={handleChange}>Tạo tài khoản</Button>
                    <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>{isLoad ? <CircularProgress color="secondary" /> : "Đăng nhập"}</Button>
                </div>
            </div>
        </div>
    )
}

export default Signin;
