import {createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import sendReq from '../helper/sendReqToServer';

export const upLoadFile = createAsyncThunk(
    "localhost:4000/upload",
    async (data, thunkAPI) => {
        const formData = new FormData();
        formData.append(
            "file",
            data,
            this.state.selectedFile.name
          );
        const configReq = {
            method: 'post',
            url: '/upload',
            header: {
                "Content-Type": "multipart/form-data"
            },
            data: data
        }
        const res = await sendReq(configReq);
        return res.data;
    }
)

export const downLoadFile = createAsyncThunk(
    "localhost:4000/download",
    async (configReq, thunkAPI) => {
        const res = await sendReq(configReq);
        return res.data;
    }
)

const file = createSlice({
    name: "file",
    initialState: {
        file: null,
        message: {
            type: null,
            mess: null
        },
        isLoading: false
    },
    reducers: {
        setMessage : (state, action) => {
            state.message = action.payload
        }
    },
    extraReducers: (builder) => {
        builder.addCase(upLoadFile.pending, (state, action) => {
            state.isLoading = true
        })
        builder.addCase(upLoadFile.fulfilled, (state, action) => {
            state.message = action.payload.message
            state.isLoading = false
            console.log(state.message)
        })
        builder.addCase(downLoadFile.pending, (state, action) => {
            state.isLoading = true
        })
        builder.addCase(downLoadFile.fulfilled, (state, action) => {
            state.file = action.payload.file
            state.message = action.payload.message
            state.isLoading = false
        })
    }
})

export const {setMessage} = file.actions;
export default file.reducer;
