import React from "react";
import { styled } from '@mui/material/styles';
import Divider from "@mui/material/Divider";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import LinkIcon from '@mui/icons-material/Link';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import DownloadIcon from '@mui/icons-material/Download';
import InfoIcon from '@mui/icons-material/Info';
import {useDispatch, useSelector} from 'react-redux';
import {setIsShowInfo} from '../../../features/currentSlice';
import { downLoadFile } from "../../../features/fileSlice";

const StyledBadge = styled(Badge)(({ theme }) => ({
    '& .MuiBadge-badge': {
      backgroundColor: '#44b700',
      color: '#44b700',
      boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
      '&::after': {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        borderRadius: '50%',
        animation: 'ripple 1.2s infinite ease-in-out',
        border: '1px solid currentColor',
        content: '""',
      },
    },
    '@keyframes ripple': {
      '0%': {
        transform: 'scale(.8)',
        opacity: 1,
      },
      '100%': {
        transform: 'scale(2.4)',
        opacity: 0,
      },
    },
  }));


const DocumentHeader = () => {
    const tabs = useSelector(state => state.current.tabs);
    const selectedFile = useSelector(state => state.current.file)
    const dispatch = useDispatch()
    const handleClick = () => {
        dispatch(setIsShowInfo());
    }
    const handleDownload = async () => {
        if(selectedFile === "") {
            return;
        }
        await dispatch(downLoadFile(selectedFile));
    }
    return(
        <>
            <div className="document-header">
                <p>{tabs === 0 ? "Tài liệu của tôi" : "Được chia sẻ với tôi"}</p>
                <div className="document-header__list-ctrl">    
                    <Tooltip title="Nhận đường liên kết">
                        <IconButton>
                            <LinkIcon />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Chia sẻ">
                        <IconButton>
                            <PersonAddIcon />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Tải về">
                        <IconButton onClick={handleDownload}>
                            <DownloadIcon />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa">
                        <IconButton>
                            <DeleteIcon />
                        </IconButton>
                    </Tooltip>
                    <Divider orientation="vertical" variant="middle" flexItem />
                    <Tooltip title="Xem chi tiết">
                        <IconButton onClick={handleClick}>
                            <InfoIcon />
                        </IconButton>
                    </Tooltip>
                </div>
            </div>
            <Divider></Divider>
        </>
    )
}

export default DocumentHeader;
