import React from "react";
import Box from '@mui/material/Box';
import Avatar from '@mui/material/Avatar';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import Logout from '@mui/icons-material/Logout';
import { styled } from '@mui/material/styles';
import Badge from '@mui/material/Badge';
import Modal from '@mui/material/Modal';
import { useSelector } from "react-redux";

const StyledBadge = styled(Badge)(({ theme }) => ({
  '& .MuiBadge-badge': {
    backgroundColor: '#44b700',
    color: '#44b700',
    boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
    '&::after': {
      position: 'absolute',
      bottom: -1,
      right: -1,
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      animation: 'ripple 1.2s infinite ease-in-out',
      border: '1px solid currentColor',
      content: '""',
    },
  },
  '@keyframes ripple': {
    '0%': {
      transform: 'scale(.8)',
      opacity: 1,
    },
    '100%': {
      transform: 'scale(2.4)',
      opacity: 0,
    },
  },
}));

const ModalFriend = ({handleClose, open}) => {
  const [value, setValue] = React.useState('')
  const user_info = useSelector(state => state.user.user.user_info);
  
  
  return (
      <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
      >
          <Box sx={style}>
              <div style={{display: 'flex', alignItems: "center", justifyContent: "space-between", padding: 5}}>
                  <h4 style={{paddingLeft: 10}}>Tìm kiếm bạn bè</h4>
                  <IconButton aria-label="search-friend" onClick={handleClose}>
                      <CloseIcon />
                  </IconButton>
              </div>
              <Divider></Divider>
              <div>   
                  <IconButton sx={{ p: '10px' }} aria-label="menu">
                      <PersonSearchIcon />
                  </IconButton>
                  <InputBase
                      sx={{ ml: 1, flex: 1, borderBottom: 1, width: "80%" }}
                      placeholder="Tên bạn bè, id"
                      inputProps={{ 'aria-label': 'Tên bạn bè' }}
                      autoFocus={true}
                      onChange={(e) => setValue(e.currentTarget.value)}
                  />
              </div>
              {!findFriendRes ? 
                  <div style={{display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                      <img src={FindFriend} alt="Find Friend" width="200px" style={{margin: "1rem"}}/>
                      <p style={{fontWeight: 500}}>Nhập ID để tìm bạn</p>
                      <sub>Kết bạn để khám phá nhiều điều mới</sub>
                  </div>
                  : Object.keys(findFriendRes).length === 0 ? 
                  <div style={{display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                      <img src={NoFriendFound} alt="No Friend Found" width="200px" style={{margin: "1rem"}}/>
                      <p style={{fontWeight: 500}}>Không tìm thấy bạn</p>
                      <sub>Hãy tìm lại</sub>
                  </div>
                  :
                  <div className="friend-info">
                      <div style={{width: "100%", display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column'}}>
                          <Avatar sx={{backgroundColor: randomColor}}></Avatar>
                          <h4>{findFriendRes[0].name}</h4>
                      </div>
                      <p>Thuộc tính</p>
                      <table className="friend-info__table">
                          <tbody>
                          <tr>
                              <td>ID</td>
                              <td>{findFriendRes[0]._id}</td>
                          </tr>
                          <tr>
                              <td>Sở hữu</td>
                              <td>{findFriendRes[0].own_files.length}</td>
                          </tr>
                          <tr>
                              <td>Chia sẻ</td>
                              <td>{findFriendRes[0].shared_files.length}</td>
                          </tr>
                          <tr>
                              <td>Bạn bè</td>
                              <td>{findFriendRes[0].friend.accepted.length}</td>
                          </tr>
                          </tbody>
                      </table>
                  </div>}
              <div style={{display: "flex", justifyContent: "flex-end", alignItems: "center", margin: 10}}>
                  <Button variant="outlined" onClick={handleClose}>Hủy</Button>
                  <Button variant="contained" onClick={handleFind}>Tìm kiếm</Button>
              </div>
          </Box>
      </Modal>
  );
}

export default function AccountMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <React.Fragment>
      <Box sx={{ display: 'flex', alignItems: 'center', textAlign: 'center' }}>
        <Tooltip title="Account settings">
          <IconButton
            onClick={handleClick}
            size="small"
            sx={{ ml: 2 }}
            aria-controls={open ? 'account-menu' : undefined}
            aria-haspopup="true"
            aria-expanded={open ? 'true' : undefined}
          >
            <StyledBadge
            overlap="circular"
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            variant="dot"
            >
              <Avatar sx={{ textTransform: "uppercase", backgroundColor: "blue" }}></Avatar>
            </StyledBadge>
          </IconButton>
        </Tooltip>
      </Box>
      <Menu
        anchorEl={anchorEl}
        id="account-menu"
        open={open}
        onClose={handleClose}
        onClick={handleClose}
        PaperProps={{
          elevation: 0,
          sx: {
            overflow: 'visible',
            filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
            mt: 1.5,
            '& .MuiAvatar-root': {
              width: 32,
              height: 32,
              ml: -0.5,
              mr: 1,
            },
            '&:before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              top: 0,
              right: 14,
              width: 10,
              height: 10,
              bgcolor: 'background.paper',
              transform: 'translateY(-50%) rotate(45deg)',
              zIndex: 0,
            },
          },
        }}
        transformOrigin={{ horizontal: 'right', vertical: 'top' }}
        anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
      >
        <MenuItem>
          <Avatar /> Thông tin tài khoản
        </MenuItem>
        <Divider />
        <MenuItem>
          <ListItemIcon>
            <Logout fontSize="small" />
          </ListItemIcon>
          Đăng xuất
        </MenuItem>
      </Menu>
    </React.Fragment>
  );
}
