import {createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import sendReq from '../helper/sendReqToServer';

export const upLoadSingleFile = createAsyncThunk(
    "localhost:4000/file/singleUpload",
    async (data, thunkAPI) => {
        const configReq = {
            method: 'post',
            url: '/file/upload/singleUpload',
            header: {},
            data: data
        }
        const res = await sendReq(configReq);
        return res.data;
    }
)

export const upLoadMultipleFile = createAsyncThunk(
    "localhost:4000/file/multipleUpload",
    async (data, thunkAPI) => {
        const configReq = {
            method: 'post',
            url: '/file/upload/multipleUpload',
            header: {},
            data: data
        }
        const res = await sendReq(configReq);
        return res.data;
    }
)

export const downLoadFile = createAsyncThunk(
    "localhost:4000/download",
    async (file, thunkAPI) => {
        const configReq = {
            method: "get",
            url: `/file/download/${file._id}`,
            responseType: "blob"
        }
        await sendReq(configReq)
        .then(async (res) => {
            const link = document.createElement("a");
            link.target = "_blank";
            link.download = file.metadata.originalname;
            link.href = URL.createObjectURL(
                new Blob([res.data], { type: `${res.data.type}` })
            );
            link.click();
        });
    }
)

export const getFile = createAsyncThunk(
    "localhost:4000/file",
    async (type,thunkAPI) => {
        const configReq = {
            method: "get",
            url: '/file',
            params: {
                type : type
            }
        }
        const res = await sendReq(configReq);
        return res.data
    }
)

const file = createSlice({
    name: "file",
    initialState: {
        file: [""],
        fileDownload: "",
        message: {
            type: null,
            mess: null
        },
        isLoading: true
    },
    reducers: {
        setMessage : (state, action) => {
            state.message = action.payload
        }
    },
    extraReducers: (builder) => {
        builder.addCase(upLoadFile.pending, (state, action) => {
            state.isLoading = true;
        })
        builder.addCase(upLoadFile.fulfilled, (state, action) => {
            state.message = action.payload.message
            state.isLoading = false;
        })
        builder.addCase(getFile.pending, (state, action) => {
            state.isLoading = true
        })
        builder.addCase(getFile.fulfilled, (state, action) => {
            state.file = action.payload.file
            state.isLoading = false
        })
    }
})

export const {setMessage} = file.actions;
export default file.reducer;
