import React, {useState, useEffect} from "react";
import { upLoadFile } from "../features/fileSlice";
import { getUserData } from "../features/userSlice";
import {useDispatch, useSelector} from 'react-redux';
import { useNavigate } from "react-router-dom";
import Header from './main-header';
import Body from './main-body';
import './css/main.css';

const Document = () => {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const uploadStatus = useSelector(state => state.file.message.type)
    const [selectFile, setSelectFile] = useState("");
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const handleSelectFile = (e) => {
        setSelectFile(e.target.files[0])
    }
    const handleClick = () => {
        const data = new FormData();
        data.append("file", selectFile, selectFile.name);
        dispatch(upLoadFile(data));
        if(uploadStatus === "Success") {
            dispatch(getUserData())
        }
        if(!auth) {
            navigate('/signin')
        }
    }
    useEffect(() => {
        dispatch(getUserData());
    }, [])
    return(
        <div className="main">
            <Header></Header>
            <Body></Body>
        </div>
    )
}

export default Document;
