import React from "react";
import DocumentTable from "./document-table";
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Divider from '@mui/material/Divider';
import Avatar from '@mui/material/Avatar';
import AvatarGroup from '@mui/material/AvatarGroup';
import IconButton from '@mui/material/IconButton';
import { useSelector, useDispatch } from "react-redux";
import { setIsShowInfo } from '../../../features/currentSlice';
import CloseIcon from '@mui/icons-material/Close';
import FileCustomIcon from "../../react-file-icon/file-icon";

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
    ({ theme, open }) => ({
      flexGrow: 1,
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginRight: -drawerWidth,
      ...(open && {
        transition: theme.transitions.create('margin', {
          easing: theme.transitions.easing.easeOut,
          duration: theme.transitions.duration.enteringScreen,
        }),
        marginRight: 0,
      }),
    }),
);
  
const DrawerHeader = styled('div')(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'space-between',
}))

const drawerWidth = 350;
var randomColor = Math.floor(Math.random()*16777215).toString(16);

const DocumentInfo = () => {
    const dispatch = useDispatch();
    const firstname = useSelector(state => state.user.user.firstname);
    const open = useSelector(state => state.current.isShowInfo);
    const currentFile = useSelector(state => state.current.file);
    console.log(currentFile)
    const handleDrawerClose = () => {
      dispatch(setIsShowInfo())
    };
    return (
      <Box sx={{ display: 'flex' }}>
        <Main open={open}>
          <DocumentTable></DocumentTable>
        </Main>
        <Drawer
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              position: 'relative'
            },
          }}
          variant="persistent"
          anchor="right"
          open={open}
        >
          <DrawerHeader>
            {currentFile._id ? <div className="file-name"><FileCustomIcon ext={currentFile.metadata.ext}></FileCustomIcon> {currentFile.metadata.originalname}</div> : <div className="file-name">Thông tin chi tiết</div>}
            <IconButton onClick={handleDrawerClose}>
              <CloseIcon></CloseIcon>
            </IconButton>
          </DrawerHeader>
          <Divider />
          {currentFile._id ?
            <div className="file-info">
              <p>Người có quyền truy cập</p>
              <div className="access-name">
                <Avatar sx={{ width: 32, height: 32, textTransform: "uppercase", backgroundColor: "blue" }}>{firstname}</Avatar>
                <Divider orientation="vertical" variant="middle" flexItem />
                <AvatarGroup max={4}>
                  {currentFile.metadata.shared.map((name) => {
                    return <Avatar sx={{ width: 32, height: 32, textTransform: "uppercase", backgroundColor: "blue" }}>{name.charAt(0)}</Avatar>
                  })}
                </AvatarGroup>
              </div>
            </div>
            :
            <div>
              
            </div>
          }
        </Drawer>
      </Box>
    )
}

export default DocumentInfo;
