import React, {useState} from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import './css/login.css'
import { sendReqToServer } from '../features/authenticateSlice';
import CircularProgress from '@mui/material/CircularProgress';
import qs from 'qs';
import $ from 'jquery';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import { setMessNull } from '../features/authenticateSlice';
import Logo from "../assets/img/document-share.png";

function Signin() {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const message = useSelector(state => state.authenticate.auth.message);
    const isLoad = useSelector(state => state.authenticate.isLoading)
    const [value, setValue] = useState(0);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const handleChange = () => {
        dispatch(setMessNull());
        setValue(value === 0 ? 1 : 0)
    };

    const handleClick = (event) => {
        if(value === 0) {
            const username = String($('#username').val());
            const password = String($('#password').val());
            if(username === "" || password === "") {
                event.preventDefault()
                return;
            }
            const configReq = {
                method: 'POST',
                url: '/account/login',
                header: 'application/x-www-form-urlencoded;charset=utf-8',
                data: qs.stringify({
                    "username": `${username}`,
                    "password": `${password}`
                })
            }
            dispatch(sendReqToServer(configReq))
            if(auth) {
                navigate('/document');
            }
        }
        else {
            const firstname = String($('#firstname').val());
            const lastname = String($("#lastname").val());
            const username = String($("#s-username").val());
            const password = String($('#s-password').val());
            const re_password = String($('#s-re_password').val());
            const configReq = {
                method: 'POST',
                url: '/account/signup',
                header: 'application/x-www-form-urlencoded;charset=utf-8',
                data: qs.stringify({
                    "firstname": `${firstname}`,
                    "lastname": `${lastname}`,
                    "username": `${username}`,
                    "password": `${password}`
                })
            }
            dispatch(sendReqToServer(configReq))
            navigate('/login');
        }
    }

    return(
        <div className='login-page'>
            <div className='login-form'>
                <img src={Logo} alt="Logo"/>
                <div className='login-form__header'>
                    <h2>Đăng nhập</h2>
                    <h4>Tiếp tục với tài khoản Dshare</h4>
                </div>
                <div className='login-form__body'>
                    {message.type === "error" && <Alert severity={message.type}>{message.mess}</Alert>}
                    <TextField id="username" label="Tên người dùng" error={message.type === "error" ? true : false} variant="outlined" className='lgf_b-textField' margin="normal"/>
                    <TextField id="password" label="Mật khẩu" error={message.type === "error" ? true : false} variant="outlined" className='lgf_b-textField'margin="normal"/>
                </div>
                <div className='login-form__bottom'>
                    <Button variant="text" size="small" className='btn-registration' onClick={handleChange}>Tạo tài khoản</Button>
                    <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>{isLoad ? <CircularProgress color="secondary" /> : "Đăng nhập"}</Button>
                </div>
            </div>
        </div>
    )
}

export default Signin;
