import React, {useEffect} from "react";
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import FolderIcon from '@mui/icons-material/Folder';
import DeleteIcon from '@mui/icons-material/Delete';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import Divider from "@mui/material/Divider";

const ListSnackBar = (props) => {
    return(
        <div style={{
            width: "100%"
        }}>
            <div>
                Upload
                <Divider></Divider>
            </div>
            <List>
                {props.fileUpload.map((item) => {
                return(
                    <ListItem
                    secondaryAction={
                        <IconButton edge="end" aria-label="delete">
                        <DeleteIcon />
                        </IconButton>
                    }
                >
                    <ListItemAvatar>
                    <Avatar>
                        <FolderIcon />
                    </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                    primary={item.name + item.progress}
                    />
                </ListItem>
                )
                })}
        </List>
        </div>
    )
}

const ListUpLoad = (props) => {
    const [state, setState] = React.useState({
        open: false,
        vertical: 'bottom',
        horizontal: 'center',
    });
    
    const { vertical, horizontal, open } = state;
    
    const handleClick = (newState) => () => {
        
        setState({ open: true, ...newState });
    };
    
    const handleClose = (e, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setState({ ...state, open: false });
    };

    useEffect(() => {
        if(props.fileUpload.length !== 0) {
            setState({
                ...state,
                open: true
            })
        }
    }, [props.fileUpload])
    
    return(
        <div className="list-upload">
            <Snackbar
                anchorOrigin={{ vertical, horizontal }}
                open={open}
                onClose={handleClose}
                message={<ListSnackBar fileUpload={props.fileUpload}></ListSnackBar>}
                key={vertical + horizontal}
                sx={{
                    backgroundColor: "white"
                }}
            />
        </div>
    )
}

export default ListUpLoad;
