import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFile } from "../../../features/fileSlice";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Loading from "../../loading";


export default function DocumentTable() {
  const dispatch = useDispatch();
  const tabs = useSelector(state => state.current.tabs);
  const isLoad = useSelector(state => state.file.isLoading);
  const file = useSelector(state => state.file.file);
  const [selected, setSelected] = React.useState();

  const handleClick = (e) => {
    setSelected(e.currentTarget.getAttribute("value"));
    console.log(selected)
  }

  const isSelected = (id) => selected === id;

  useEffect(() => {
    dispatch(getFile(tabs === 0 ? "own" : "shared"));
  }, [tabs])

  if(isLoad) {
    return(<Loading></Loading>)
  }
  else return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650, boxShadow: 'none' }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Tên</TableCell>
            <TableCell align="center">Chủ sở hữu</TableCell>
            <TableCell align="center" >Thời gian tải lên</TableCell>
            <TableCell align="center">Kích cỡ tệp</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {file.map(file => {
            const isItemSelected = isSelected(file._id);
            <TableRow
              hover
              onClick={handleClick}
              key={file.filename}
              value={file._id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              selected=""
            >
              <TableCell component="th" scope="row">
                {file.metadata.originalname}
              </TableCell>
              <TableCell align="center">{file.metadata.own}</TableCell>
              <TableCell align="center">{Date(file.uploadDate)}</TableCell>
              <TableCell align="center">{file.carbs}</TableCell>
            </TableRow>
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
