import React from 'react';
import './css/login.css'
import Alert from '@mui/material/Alert';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import InputAdornment from '@mui/material/InputAdornment';
import { sendReqToServer, setMessNull } from '../features/authenticateSlice';
import qs from 'qs';
import $ from 'jquery';
import Logo from "../assets/img/document-share.png";

const Signup = () => {
    const message = useSelector(state => state.authenticate.auth.message);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const handleChange = () => {
        dispatch(setMessNull());
        navigate('/signin');
    }
    const handleClick = () => {
        const firstname = String($('#firstname').val());
        const lastname = String($("#lastname").val());
        const username = String($("#s-username").val());
        const password = String($('#s-password').val());
        const re_password = String($('#s-re_password').val());
        const configReq = {
            method: 'POST',
            url: '/account/signup',
            header: 'application/x-www-form-urlencoded;charset=utf-8',
            data: qs.stringify({
                "firstname": `${firstname}`,
                "lastname": `${lastname}`,
                "username": `${username}`,
                "password": `${password}`
            })
        }
        dispatch(sendReqToServer(configReq))
        //navigate('/signin');
    }
    return(
        <div className='account-page'>
            <form className='account-form'>
                <img src={Logo} alt="Logo"/>
                <div className='form__header'>
                    <h2>Đăng ký</h2>
                    <h4>Tạo tài khoản Dshare</h4>
                </div>
                <div className='form__body'>
                    {message.isErr && <Alert severity="error">{message.mess}</Alert>}
                    <div className='acf-row'>
                        <TextField id='lastname' label="Họ" variant="outlined" className='lgf_b-textField' margin="normal" size='small'/>
                        <TextField id='firstname' label="Tên" variant="outlined" className='lgf_b-textField'margin="normal" size='small'/>
                    </div>
                    <TextField id='s-username' label="Tên người dùng" variant="outlined" className='lgf_b-textField'margin="normal" InputProps={{startAdornment: <InputAdornment position="start">@</InputAdornment>,}} size="small" />
                    <span>Bạn có thể sử dụng số và dấu chấm</span>
                    <div className='acf-row'>
                        <TextField id='s-password' label="Mật khẩu" variant="outlined" className='lgf_b-textField' margin="normal" size='small'/>
                        <TextField id='s-re_password' label="Xác nhận" variant="outlined" className='lgf_b-textField'margin="normal" size='small'/>
                    </div>
                    <span>Tối thiểu 8 ký tự bao gồm cả chữ cái và chữ số</span>
                </div>
                <div className='form__bottom'>
                    <Button variant="text" size="small" className='btn-registration' onClick={handleChange}>Đăng nhập ngay</Button>
                    <Button variant="contained" size="medium" className='btn-login' onClick={handleClick}>Đăng ký</Button>
                </div>
            </form>
        </div>
    )
}

export default Signup;
