import React from "react";
import Error_500 from "../assets/img/drible.gif";
import Error_404_Img from "../assets/img/error_404.gif";
import { Button } from "@mui/material";
import { useNavigate } from "react-router-dom";

const Error = ({status}) =>{
    const navigate = useNavigate()
    if(status === 404) {
        return(
            <div style={{
                width: "100vw",
                height: "100vh",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: 'column'
            }}>
                <img width="50%" height="40%" style={{objectFit: "fill"}} src={Error_404_Img} alt="404" />
                <h3 style={{margin: 10}}>Không tìm thấy trang yêu cầu</h3>
                <Button variant="contained" onClick={() => navigate("/")}>Trở về trang chủ</Button>
            </div>
        )
    }
    else {
        return(
            <div style={{
                width: "100vw",
                height: "100vh",
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
            }}>
                <img width="60%" height="70%" style={{objectFit: "fill"}} src={Error_500} alt="Error" />
            </div>
        )
    }
}

export default Error;
