import React from 'react';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import { useSelector } from 'react-redux';

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const Message = (props) => {
    const message = useSelector(state => state.file.message);
    const isLoad = useSelector(state => state.file.isLoading);
  return (
      <div>
        <Snackbar open={props.open} autoHideDuration={6000} onClose={props.handleClose} key={"bottomright"}>
            <Alert onClose={props.handleClose} severity={message.type} sx={{ width: '100%' }}>
                {message.mess}
            </Alert>
        </Snackbar>
      </div>
  );
}

export default Message;
