import React from "react";
import { FileIcon, defaultStyles } from "react-file-icon";
import { ReactComponent as IcFileHwp } from "./images/ic_file_hwp.svg";
import { styleDefObj } from "./style-customize";

const extensionList = Object.keys(defaultStyles);

extensionList.push("abc", "ddd");

const customIcons = [
    {
      ext: "hwp",
      Component: IcFileHwp
    }
];

const FileCustomIcon = (props) => {
    const customDefaultLabelColor = styleDefObj[props.ext]
          ? styleDefObj[props.ext]["labelColor"] ?? "#777"
          : "#777";

        // Library defined default labelCOlor
        const libDefaultGlyphColor =
          defaultStyles[ext] && defaultStyles[ext]["labelColor"];
    return(
        <>
        {extensionList.map((ext) => {
        // If custom style def do not have labelColor, use #777
        const customDefaultLabelColor = styleDefObj[ext]
          ? styleDefObj[ext]["labelColor"] ?? "#777"
          : "#777";

        // Library defined default labelCOlor
        const libDefaultGlyphColor =
          defaultStyles[ext] && defaultStyles[ext]["labelColor"];

        return (
          <div className="icon">
            <FileIcon
              extension={ext}
              glyphColor={libDefaultGlyphColor ?? customDefaultLabelColor}
              labelColor={customDefaultLabelColor}
              {...defaultStyles[ext]}
              {...styleDefObj[ext]}
            />
          </div>
        );
      })}
    </>)
}

export default FileCustomIcon;
