import React from "react";
import { styled, alpha } from '@mui/material/styles';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import DriveFolderUploadIcon from '@mui/icons-material/DriveFolderUpload';
import Divider from '@mui/material/Divider';
import CreateNewFolderIcon from '@mui/icons-material/CreateNewFolder';
import AddIcon from '@mui/icons-material/Add';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import { upLoadFile, getFile } from "../../../features/fileSlice";
import { useDispatch, useSelector } from "react-redux";
import zip from 'js'

const Input = styled('input')({
  display: 'none',
});

const StyledMenu = styled((props) => (
  <Menu
    elevation={0}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'right',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'right',
    }}
    {...props}
  />
))(({ theme }) => ({
  '& .MuiPaper-root': {
    borderRadius: 6,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color:
      theme.palette.mode === 'light' ? 'rgb(55, 65, 81)' : theme.palette.grey[300],
    boxShadow:
      'rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px',
    '& .MuiMenu-list': {
      padding: '4px 0',
    },
    '& .MuiMenuItem-root': {
      '& .MuiSvgIcon-root': {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      '&:active': {
        backgroundColor: alpha(
          theme.palette.primary.main,
          theme.palette.action.selectedOpacity,
        ),
      },
    },
  },
}));

export default function Menus(props) {
  const dispatch = useDispatch();
  const type = useSelector(state => state.file.message.type);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleChange = async (e) => {
    const data = new FormData();
    data.append("path", "/a");
    data.append("file", e.target.files[0], e.target.files[0].name);
    await dispatch(upLoadFile(data))
    await props.handleClick();
    dispatch(getFile("own"))
    handleClose()
  }

  const upLoadFolder = async (e) => {
    const data = new FormData();
  }

  return (
    <div>
      <Button
        id="demo-customized-button"
        aria-controls={open ? 'demo-customized-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        variant="contained"
        disableElevation
        onClick={handleClick}
        startIcon={<AddIcon />}
        sx = {{margin: 2}}
      >
        Thêm
      </Button>
      <StyledMenu
        id="demo-customized-menu"
        MenuListProps={{
          'aria-labelledby': 'demo-customized-button',
        }}
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
      >
        <MenuItem onClick={handleClose} disableRipple>
          <CreateNewFolderIcon />
          Tạo thư mục
        </MenuItem>
        <Divider sx={{ my: 0.5 }} />
        <MenuItem disableRipple>
          <label htmlFor="icon-button-file">
              <Input accept="*" id="icon-button-file" type="file" name="file" onChange={handleChange}/>
              <UploadFileIcon />
              Tải lên tệp tin
          </label>
        </MenuItem>
        <MenuItem onClick={handleClose} disableRipple>
          <label htmlFor="icon-button-folder">
          <input type="file" id="filepicker" name="fileList" webkitdirectory='true' directory='true' multiple onChange={upLoadFolder}/>
              <DriveFolderUploadIcon />
              Tải lên thư mục
          </label>
        </MenuItem>
      </StyledMenu>
    </div>
  );
}
