import React from "react";
import Menu from './side-bar/file-menu';
import ListCtrl from "./side-bar/list-ctrl";
import Message from "./side-bar/message";
import { setIsShowInfo } from '../../../features/currentSlice';

const SideBar = () => {
    const [open, setOpen] = React.useState(false);

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpen(false);
    };

    return(
        <div className="side-bar" onClick={handleClickSet}>
            <Menu handleClick={handleClick}></Menu>
            <Message handleClose={handleClose} handleClick={handleClick} open={open}></Message>
            <ListCtrl></ListCtrl>
        </div>
    )
}

export default SideBar;
