import {createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import sendReq from '../helper/sendReqLogin'

export const sendReqToServer = createAsyncThunk(
    "Req Login to Server",
    async (configReq, thunkAPI) => {
        const res = await sendReq(configReq)
        return res.data
    }
)

const authenticate = createSlice({
    name: "authenticate",
    initialState: {
        auth : {
            isAuthenticated: false,
            message: {
                type: null,
                mess: null
            }
        },
        isLoading : false
    },
    reducers : {
        setStateAuth : (state, action) => {
            state.isAuthenticated = action.payload.isAuthenticated;
            state.message = action.payload.mess
        },
        setIsLoading : (state, action) => {
            state.isLoading = action.payload
        }
    },
    extraReducers : (builder) => {
        builder.addCase(sendReqToServer.fulfilled, (state, action) => {
            state.auth = action.payload;
            setTimeout(() => {
                state.isLoading = false;
            })
        })
    }
})

export const {setStateAuth, setIsLoading} = authenticate.actions;
export default authenticate.reducer;
