import React from "react";
import Divider from "@mui/material/Divider";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import LinkIcon from '@mui/icons-material/Link';
import GroupAddIcon from '@mui/icons-material/GroupAdd';
import CloseIcon from '@mui/icons-material/Close';
import DownloadIcon from '@mui/icons-material/Download';
import InfoIcon from '@mui/icons-material/Info';
import {useDispatch, useSelector} from 'react-redux';
import {setIsShowInfo} from '../../../features/currentSlice';
import { downLoadFile } from "../../../features/fileSlice";
import Breadcrumbs from '@mui/material/Breadcrumbs';
import Link from '@mui/material/Link';
import {Modal, Box, Button} from "@mui/material";
import { setPath } from "../../../features/currentSlice";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    borderRadius: 1,
    boxShadow: 24
};

const ShareModal  = ({handleCloseShareModal, openShareModal}) => {
    return(
        <Modal
            open={openShareModal}
            onClose={handleCloseShareModal}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
            >
            <Box sx={style}>
            <div style={{display: 'flex', alignItems: "center", justifyContent: "space-between", padding: 5}}>
                        <h4 style={{paddingLeft: 10}}>Tạo thư mục mới</h4>
                        <IconButton aria-label="search-friend" onClick={handleCloseShareModal}>
                            <CloseIcon />
                        </IconButton>
                </div>
                <Divider></Divider>
                <form >
                
                </form>
            </Box>
        </Modal>
    )
}


const DocumentHeader = () => {
    const tabs = useSelector(state => state.current.tabs);
    const selectedFile = useSelector(state => state.current.file);
    const path = useSelector(state =>  state.current.path);
    const [openShareModal, setOpenShareModal] = React.useState(false);
    const dispatch = useDispatch()

    const handleClick = () => {
        dispatch(setIsShowInfo());
    }

    const  handleClickLink = (e) => {
        e.preventDefault()

    }

    const handleDownload = async () => {
        if(selectedFile === "") {
            return;
        }
        await dispatch(downLoadFile(selectedFile));
    }

    const handleShare = async () => {
        setOpenShareModal(true);
    }

    const handleCloseShareModal = () => {
        setOpenShareModal(false)
    }

    return(
        <>
            <div className="document-header">
                {tabs === 0 ? <Breadcrumbs>
                    <Link underline="hover" color="inherit" onClick={handleClickLink} sx={{cursor: 'pointer'}}>
                        Tài liệu của tôi
                    </Link>
                    {path.split('/').map((value, index) => {
                        
                        if(value==="") return;
                        else if(path.split('/').length - 1 === index) {
                            return <>{value}</>;
                        }
                        else {
                            return(
                                <Link underline="hover" color="inherit" onClick={handleClickLink} sx={{cursor: 'pointer'}}>
                                    {value}
                                </Link>
                            )
                        }
                    })}
                </Breadcrumbs> : "Được chia sẻ với tôi"}
                <div className="document-header__list-ctrl">
                    <ShareModal handleCloseShareModal={handleCloseShareModal} openShareModal={openShareModal}></ShareModal>   
                    {selectedFile._id ?
                    <div style={{width: "auto", height: "100%", textAlign: "center", verticalAlign: "center"}}>
                        <Tooltip title="Nhận đường liên kết">
                            <IconButton>
                                <LinkIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Chia sẻ">
                            <IconButton onClick={handleShare}>
                                <GroupAddIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Tải về">
                            <IconButton onClick={handleDownload}>
                                <DownloadIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Xóa">
                            <IconButton>
                                <DeleteIcon />
                            </IconButton>
                        </Tooltip>
                        <Divider orientation="vertical" variant="middle" flexItem />
                    </div> : ""}
                    <Tooltip title="Xem chi tiết">
                        <IconButton onClick={handleClick}>
                            <InfoIcon />
                        </IconButton>
                    </Tooltip>
                </div>
            </div>
            <Divider></Divider>
        </>
    )
}

export default DocumentHeader;
