import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFile } from "../../../features/fileSlice";
import { getCurrentFile } from "../../../features/currentSlice";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Loading from "../../loading";
import moment from "moment";
import FileCustomIcon from "../../react-file-icon/file-icon";
import FolderIcon from '../../../assets/img/folder.svg';


export default function DocumentTable() {
  const dispatch = useDispatch();
  const tabs = useSelector(state => state.current.tabs);
  const isLoad = useSelector(state => state.file.isLoading);
  const file = useSelector(state => state.file.file);
  const selectedFile = useSelector(state => state.current.file)
  console.log(selectedFile)

  const handleClick = async (e) => {
    console.log("CLick")
    if(e.currentTarget.getAttribute("value")===selectedFile._id) return;
    await dispatch(getCurrentFile(e.currentTarget.getAttribute("value")))
  }

  const handleDoubleClick = () => {
    if(selectedFile.metadata.isDir) {
      console.log("DoubleClick")
    }
    
  }

  const isItemSelect = (id) => selectedFile._id === id;

  useEffect(() => {
    dispatch(getFile(tabs === 0 ? "own" : "shared",));
  }, [tabs])

  if(isLoad) {
    return(<Loading></Loading>)
  }
  else return (
    <Paper sx={{overflow: "hidden"}}>
      <TableContainer className="table-container">
      <Table sx={{ minWidth: 650, boxShadow: 'none', overflow: "scroll" }} stickyHeader aria-label="sticky table">
        <TableHead>
          <TableRow>
            <TableCell>Tên</TableCell>
            <TableCell align="center">Chủ sở hữu</TableCell>
            <TableCell align="center" >Thời gian tải lên</TableCell>
            <TableCell align="center">Kích cỡ tệp</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {file.map((file) => (
            
            <TableRow
              hover
              onClick={handleClick}
              onDoubleClick={handleDoubleClick}
              key={file.metadata.isDir ? file.name : file.filename}
              value={file._id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 }, 'th': {fontWeight: "bold"} }}
              selected = {isItemSelect(file._id)}
            >
              <TableCell component="th" scope="row" sx={{display: 'flex', maxHeight: 100, alignItems: "center"}}>
                { file.metadata.isDir ?
                  <div className="icon">
                    {console.log(file)}
                    <img src={FolderIcon} style={{width: '100%'}} alt="folder"/>
                  </div>
                  :<FileCustomIcon ext={file.metadata.ext}></FileCustomIcon>
                }
                {file.metadata.isDir ? file.name : file.metadata.originalname}
              </TableCell>
              <TableCell align="center">{file.metadata.own}</TableCell>
              <TableCell align="center">{moment(file.uploadDate).utc().format("MMM Do, YYYY")}</TableCell>
              <TableCell align="center">{Math.round(file.length/1024) < 1 || file.metadata.isDir ? "-" : Math.round(file.length/1024) + " KB"}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      </TableContainer>
    </Paper>
  );
}
