import React, {useState, useEffect} from "react";
import sendReq from "../helper/sendReqToServer";
import { getUserData } from "../features/userSlice";
import {useDispatch, useSelector} from 'react-redux';
import { useNavigate } from "react-router-dom";

const Document = () => {
    const auth = useSelector((state) => state.authenticate.auth.isAuthenticated);
    const [selectFile, setSelectFile] = useState("");
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const handleSelectFile = (e) => {
        setSelectFile(e.target.files[0])
    }
    const handleClick = () => {
        const data = new FormData();
        data.append("file", selectFile, selectFile.name);

        const reqConfig = {
            method: 'post',
            url: '/upload',
            header: {
                "Content-Type": "multipart/form-data"
            },
            data: data
        }
        sendReq(reqConfig);
        if(!auth) {
            navigate('/signin')
        }
    }
    useEffect(() => {
        const configReq = {
            method: 'get',
            url: '/user',
            header: {
                "Content-Type": "text/plain"
            },
            data: ""
        }
        dispatch(getUserData(configReq));
    }, [])
    return(
        <>
            <h1>Document here</h1>
            <label htmlFor="contained-button-file">
            <input accept="*" type="file" onChange={handleSelectFile}/>
            {/*<input accept="image/*" type="file" webkitdirectory='true' mozdirectory='true' directory='true' onChange={handleSelectFile}/>*/}
            <button type="button" onClick={handleClick}>
                Upload
            </button>
            </label>
        </>
    )
}

export default Document;
