import React from "react";
import { useLocation } from "react-router-dom";
import Error_500 from "../assets/img/error_500.gif";

const Error = () =>{
    const { state } = useLocation();
    if(state === 500) {
        return(
            <>
                <img width={"100vh"} src={Error_500} alt="Error" />
            </>
        )
    }
    else {
        return(
            <>
            </>
        )
    }
}

export default Error;
