import React from "react";
import Menu from './side-bar/file-menu';
import ListCtrl from "./side-bar/list-ctrl";
import Message from "./side-bar/message";
import { setCurrentFile } from "../../features/currentSlice";
import { useDispatch } from "react-redux";

const SideBar = () => {
    const [open, setOpen] = React.useState(false);
    const dispatch = useDispatch();
    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpen(false);
    };
    const handleClickSideBar = () => {
        dispatch(setCurrentFile({
            _id: null
        },))
    }
    return(
        <div className="side-bar" onClick={handleClickSideBar}>
            <Menu handleClick={handleClick}></Menu>
            <Message handleClose={handleClose} handleClick={handleClick} open={open}></Message>
            <ListCtrl></ListCtrl>
        </div>
    )
}

export default SideBar;
