import React, {useState} from 'react';
import './css/login.css'
import Alert from '@mui/material/Alert';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import InputAdornment from '@mui/material/InputAdornment';
import { sendReqToServer, setMess,  } from '../features/authenticateSlice';
import qs from 'qs';
import $ from 'jquery';
import Logo from "../assets/img/document-share.png";

const Signup = () => {
    const message = useSelector(state => state.authenticate.auth.message);
    const [typeField, setTypeField] = useState({
        firstname: false,
        lastname: false,
        username: false
    })
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const handleChange = () => {
        dispatch(setMess({
            type: null,
            mess: null
        }));
        navigate('/signin');
    }
    const checkSignIn = (data) => {
        const username_regex = /^[A-Za-z0-9._]{1,15}$/;
        const password_regex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d@$!%*?&]{8,15}$/;
        if(data.firstname === "" || data.lastname === "") {
            setTypeField({
                ...typeField,
                firstname: true,
                lastname: true
            })
            return dispatch(setMess({
                type: "error",
                mess: "Họ hoặc tên không được để trống !"
            }))
        }
        if(!username_regex.test(data.username)) {
            setTypeField({
                firstname: false,
                lastname: false,
                username: true,
                password
            })
            return dispatch(setMess({
                type: "error",
                mess: "Tên người dùng không đúng định dạng !"
            }))
        }
        if(!password_regex.test(data.password)) {
            return dispatch(setMess({
                type: "error",
                mess: "Mật khẩu không đúng định dạng !"
            }))
        }
        if(data.password != data.re_password) {
            return dispatch(setMess({
                type: "error",
                mess: "Xác nhận mật khẩu không đúng !"
            }))
        }
        return true;
    }
    const handleClick = () => {
        const firstname = String($('#firstname').val());
        const lastname = String($("#lastname").val());
        const username = String($("#s-username").val());
        const password = String($('#s-password').val());
        const re_password = String($('#s-re_password').val());
        const data = {
            firstname: firstname,
            lastname: lastname,
            username: username,
            password: password,
            re_password: re_password,
        }
        console.log(checkSignIn(data));
        if(false) {
            const configReq = {
                method: 'POST',
                url: '/account/signup',
                header: 'application/x-www-form-urlencoded;charset=utf-8',
                data: qs.stringify({
                    "firstname": `${firstname}`,
                    "lastname": `${lastname}`,
                    "username": `${username}`,
                    "password": `${password}`
                })
            }
            dispatch(sendReqToServer(configReq))
        }
        //navigate('/signin');
    }
    return(
        <div className='account-page'>
            <div className='account-form'>
                <div className='account-form__img'>
                    <img src=''/>
                </div> 
                <form className='account-form__form'>
                    <img src={Logo} alt="Logo"/>
                    <div className='form__header'>
                        <h2>Đăng ký</h2>
                        <h4>Tạo tài khoản Dshare</h4>
                    </div>
                    <div className='form__body'>
                    {message.type != null && <Alert severity={message.type}>{message.mess}</Alert>}
                        <div className='acf-row'>
                            <TextField id='lastname' label="Họ" variant="outlined" error={typeField.firstname} className='acf_b-textField' margin="normal" size='small'/>
                            <TextField id='firstname' label="Tên" variant="outlined" error={typeField.firstname} className='acf_b-textField'margin="normal" size='small'/>
                        </div>
                        <TextField id='s-username' label="Tên người dùng" variant="outlined" className='acf_b-textField'margin="normal" InputProps={{startAdornment: <InputAdornment position="start">@</InputAdornment>,}} size="small" />
                        <span>Từ 8 đến 10 ký tự, bạn có thể sử dụng số và dấu chấm</span>
                        <div className='acf-row'>
                            <TextField id='s-password' label="Mật khẩu" variant="outlined" className='acf_b-textField' margin="normal" size='small'/>
                            <TextField id='s-re_password' label="Xác nhận" variant="outlined" className='acf_b-textField'margin="normal" size='small'/>
                        </div>
                        <span>Từ 8 đến 15 ký tự bao gồm cả chữ cái và chữ số</span>
                    </div>
                    <div className='form__bottom'>
                        <Button variant="contained" size="medium" className='btn-submit' onClick={handleClick}>Đăng ký</Button>
                        <span>Bạn đã có tài khoản ?</span>
                        <Button variant="text" size="small" className='btn-registration' onClick={handleChange}>Đăng nhập ngay</Button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Signup;
