import React, {useEffect} from 'react';
import Login from './component/login'
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import PrivateRoute from './route/private_route'
import ReRoute from './route/redirect_route';
import Document from "./component/document"
import sendReqLogin from "../helper/sendReqLogin";

function App() {
  useEffect(async () => {
    const configReq = {
      method: 'get',
      url: '/authenticate',
      header: 'application/x-www-form-urlencoded;charset=utf-8',
    }
    const res = await sendReqLogin(configReq);
      if(res.data.isAuthenticated) {
          navigate('/document');
      }
    })
  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<Navigate to="/document"></Navigate>} ></Route>

          <Route path='/document' element={
            <PrivateRoute>
              <Document></Document>
            </PrivateRoute>
            }></Route>

          <Route path='/login' element={
            <ReRoute>
              <Login></Login>
            </ReRoute>
          }></Route>

        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
