import React from "react";
import Error_500 from "../assets/img/drible.gif";
import Error_404_Img from "../assets/img/error_404.gif";
import { Button } from "@mui/material";

const Error = ({status}) =>{
    if(status === 404) {
        return(
            <div style={{
                width: "100vw",
                height: "100vh",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: 'column'
            }}>
                <img width="40%" height="50%" style={{objectFit: "fill"}} src={Error_404_Img} alt="404" />
                <h3>Không tìm thấy trang yêu cầu</h3>
                <Button>Trở về trang chủ</Button>
            </div>
        )
    }
    else {
        return(
            <div style={{
                width: "100vw",
                height: "100vh",
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
            }}>
                <img width="60%" height="70%" style={{objectFit: "fill"}} src={Error_500} alt="Error" />
            </div>
        )
    }
}

export default Error;
