import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFile } from "../../../features/fileSlice";
import { setFile } from "../../../features/currentSlice";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Loading from "../../loading";


export default function DocumentTable() {
  const dispatch = useDispatch();
  const tabs = useSelector(state => state.current.tabs);
  const isLoad = useSelector(state => state.file.isLoading);
  const file = useSelector(state => state.file.file);
  const selectedFile = useSelector(state => state.current.file)

  const handleClick = (e) => {
    dispatch(setFile(e.currentTarget.getAttribute("value")))
  }

  const isItemSelect = (id) => selectedFile === id;

  useEffect(() => {
    dispatch(getFile(tabs === 0 ? "own" : "shared"));
  }, [tabs])

  if(isLoad) {
    return(<Loading></Loading>)
  }
  else return (
    <Paper></Paper>
    
  );
}
