import React from "react";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getFriend, handleFriend } from "../../../features/userSlice";
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Loading from "../../loading";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import Divider from "@mui/material/Divider";

function TabPanel(props) {
    const { children, value, index, ...other } = props;
  
    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box sx={{ p: 2 }}>
            {children}
          </Box>
        )}
      </div>
    );
}

function a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
}

const ButtonHandleFriend = ({handleAdd, handleDelete, index}) => {
    if(index === 0) {
        return(<>
            <Button
            onClick={handleDelete}
        >Xóa bạn bè</Button>
        </>)
    }
    if(index===1) {
        return(<>
            <Button
                onClick={handleAdd}
            >Chấp nhận</Button>
            <Button
            onClick={handleDelete}
            >Xóa yêu cầu</Button>
        </>)
    }
    else return(<>
        <Button
        onClick={handleDelete}
        >Hủy yêu cầu</Button>
    </>)
}

const FriendTable = ({friend, index}) => {
    const dispatch = useDispatch();
    console.log(friend)
    const handleAdd = (id) => {
        if(index === 1) {
            dispatch(handleFriend({type: "acceptRequest", id}))
        }
    }
    const handleDelete = (id) => {
        if(index === 0) {
            console.log("deleteFriend", id)
            dispatch(handleFriend({type: "deleteFriend", id}))
        }
        else if(index === 1) {
            dispatch(handleFriend({type: "deleteRequest", id}))
        }
        else if(index === 2) {
            dispatch(handleFriend({type: "deleteMyRequest", id}))
        }
    }
    if(friend.length === 0) {
        return "Không có bạn"
    }
    return(<>
        <List dense sx={{ width: '100%', bgcolor: 'background.paper' }}>
        {friend.map((value) => {
            const labelId = `checkbox-list-secondary-label-${value._id}`;
            return (
            <ListItem
                key={value._id}
                secondaryAction={
                    <>
                        {<ButtonHandleFriend handleAdd={() => handleAdd(value._id)} handleDelete={() => handleDelete(value._id)} index={index}></ButtonHandleFriend>}
                    </>
                }
                disablePadding
            >
                <ListItemButton>
                <ListItemAvatar>
                    <Avatar></Avatar>
                </ListItemAvatar>
                <ListItemText id={labelId} primary={value.name} />
                </ListItemButton>
                <Divider></Divider>
            </ListItem>
            );
        })}
        </List>
    </>)
}

const Friend = () => {
    const dispatch = useDispatch();
    const isLoad = useSelector(state => state.user.isLoading)
    const friend = useSelector(state => state.user.friend);
    const [value, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    useEffect(() => {
        const typeArray = ["friend", "reqFriend", "myReqFriend"]
        dispatch(getFriend(typeArray[value]));
    }, [value])
    if(isLoad) {
        return <Loading></Loading>
    }
    else return(
        <>
            <div>
                <Box sx={{ width: '100%' }}>
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
                    <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Bạn bè" {...a11yProps(0)} />
                    <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Yêu cầu kết bạn" {...a11yProps(1)} />
                    <Tab sx={{textTransform: 'none', fontWeight: 600}} label="Lời mời đã gửi" {...a11yProps(2)} />
                    </Tabs>
                </Box>
                    <TabPanel value={value} index={0}>
                        <FriendTable friend={friend.friend} index={0} type="accepted"></FriendTable>
                    </TabPanel>
                    <TabPanel value={value} index={1}>
                        <FriendTable friend={friend.friend} index={1} type="request"></FriendTable>
                    </TabPanel>
                    <TabPanel value={value} index={2}>
                        <FriendTable friend={friend.friend} index={2} type="myRequest"></FriendTable>
                    </TabPanel>
                </Box>
                
            </div>
        </>
    )
}

export default Friend;
